import React from "react";
import { useNavigate } from "react-router-dom";
import "./dashboard.css";
import { database } from "../../dtb/firebase"; 
import { ref, onValue } from "firebase/database";

export default function Dashboard() {
  const navigate = useNavigate();
  const [data, setData] = useState({});

  useEffect(() => {
    const dbRef = ref(database, "sensors");

    // L·∫Øng nghe realtime
    onValue(dbRef, (snapshot) => {
      const value = snapshot.val();
      setData(value);
      setData(value || {});
    });
  }, []);

  return (
    <div className="dashboard-container">
      <h1>ƒê√ÇY L√Ä TRANG DASHBOARD</h1>

      {/* N√∫t quay l·∫°i mainpage */}
      <button onClick={() => navigate("/")}>
        Quay l·∫°i Main Page
      </button>
      <h1>IoT Dashboard</h1>
      <p>üå° Temperature: {data.temperature}</p>
      <p>üíß Humidity: {data.humidity}</p>
      <p>üí° Light: {data.light}</p>
      <p>ü™† Pump status: {data.pump ? "ON" : "OFF"}</p>
    </div>
  );
}





